import { Component } from '@angular/core';
import { Appointment } from '../models/appointment';
import { OnInit } from '@angular/core';

@Component({
  selector: 'app-appointment-list',
  templateUrl: './appointment-list.component.html',
  styleUrls: ['./appointment-list.component.css'],
})
export class AppointmentListComponent implements OnInit{


  newAppointmentTitle: string = '';

  newAppointmentDate: Date = new Date();

  appointments: Appointment[] = [];

  ngOnInit(): void {
    let savedAppointments = localStorage.getItem("appointments")

    this.appointments = savedAppointments ? JSON.parse(savedAppointments) : []
  }

  /**
   * How to push/add to an array
   */

  addAppointment() {
    if (this.newAppointmentTitle.trim().length && this.newAppointmentDate) {
      let newAppointment: Appointment = {
        id: Date.now(),
        title: this.newAppointmentTitle,
        date: this.newAppointmentDate,
      };

      this.appointments.push(newAppointment);

      this.newAppointmentTitle = '';
      this.newAppointmentDate = new Date();

      localStorage.setItem('appointments', JSON.stringify(this.appointments)); // saves entire array locally
    }
  }

  /**
   * How to delete from an array
   */

  deleteAppointment(index: number) {
    this.appointments.splice(index, 1);
    localStorage.setItem('appointments', JSON.stringify(this.appointments));
  }
}
<div class="container">
  <div class="card mt-3">
    <div class="row">
      <div class="col">
        <h2 class="text-center">Create New Appointment</h2>
      </div>
    </div>

    <div class="row mt-3">
      <div class="col-md-6">
        <input
          class="form-control"
          [(ngModel)]="newAppointmentTitle"
          placeholder="Appointment Description"
        />
      </div>

      <div class="col-md-4">
        <input
          class="form-control"
          [(ngModel)]="newAppointmentDate"
          type="date"
          placeholder="Appointment Date"
        />
      </div>

      <div class="col-md-2">
        <button class="btn btn-primar w-100" (click)="addAppointment()">
          Add
        </button>
      </div>
    </div>
  </div>

  <h2 class="text-center mt-5">Appointments</h2>
  <ul class="">
    <li *ngFor="let appointment of appointments; index as i">
      {{ appointment.title }}
      {{ appointment.date | date : "MM.dd.yyyy" }}
      <button (click)="deleteAppointment(i)">Delete Appointment</button>
    </li>
  </ul>

</div>
